mapper.update:
update t_brand set update_date=now() , update_by = 'system'

	, brand_name = #{brandName}


	, brand_ename = #{brandEname}


	, of_origin = #{ofOrigin}


	, is_use = #{isUse}

where brand_id = #{brandId}

mapper.select:
select brand_id, brand_name, brand_ename, of_origin, is_use, create_date, create_by, update_date, update_by, date_format(create_date, '%Y年%m月%d日 %T') as createDateFormat, date_format(update_date, '%Y年%m月%d日 %T') as updateDateFormat from t_brand where 1=1
and of_origin = #{ofOrigin}
and is_use = #{isUse}
and brand_name like '%${brandName}%'
and brand_ename like '%${brandEname}%'
order by create_date desc
