mapper.update:
update t_check set update_date = now()

	, tracking_number = #{trackingNumber}


	, order_time = #{orderTime}


	, transfer_company = #{transferCompany}


	, order_addr = #{orderAddr}


	, brand_id = #{brandId}


	, series_id = #{seriesId}


	, single_id = #{singleId}


	, num = #{num}


	, unit_price = #{unitPrice}


	, remark = #{remark}


	, payby = #{payby}


	, order_status = #{orderStatus}

	where id = #{id}

mapper.select:
	select id, tracking_number, order_time, transfer_company, order_addr, brand_id, series_id, single_id, num, unit_price, remark, payby, order_status, create_date, update_date, date_format(create_date, '%Y年%m月%d日 %T') as createDateFormat, date_format(update_date, '%Y年%m月%d日 %T') as updateDateFormat, FORMAT(num*unit_price ,2) as sumprice  
	,(select brand_name from t_brand where brand_id = t.brand_id)	 as brand_name
    ,(select series_name from t_series where series_id = t.series_id) as series_name
    ,(select single_name from t_singleproduct where single_id = t.single_id) as single_name
	from t_check t
	where 1=1 
	
		and tracking_number = #{param1.trackingNumber}
	
	
		and order_time = #{param1.orderTime}
	
	
		and transfer_company = #{param1.transferCompany}
	
	
		and order_addr = #{param1.orderAddr}
	
	
		and brand_id = #{param1.brandId}
	
	
		and series_id = #{param1.seriesId}
	
	
		and single_id = #{param1.singleId}
	
	
		and payby = #{param1.payby}
		
	
		and order_status = #{param1.orderStatus}
	
	
		and DATE_FORMAT(create_date, '%Y%m%d') >= #{param2}
	
	
		and DATE_FORMAT(create_date, '%Y%m%d') <= #{param3}
