mapper.update:
update t_series set update_date=now() , update_by = 'system'

	, series_name = #{seriesName}


	, series_ename = #{seriesEname}


	, of_origin = #{ofOrigin}


	, is_use = #{isUse}

where series_id = #{seriesId}

mapper.select:
select series_id, series_name, series_ename, of_origin, (select brand_name from t_brand where brand_id = t_series.of_origin) as of_origin_name, is_use, create_date, create_by, update_date, update_by, date_format(create_date, '%Y年%m月%d日 %T') as createDateFormat, date_format(update_date, '%Y年%m月%d日 %T') as updateDateFormat from t_series where 1=1
and of_origin = #{ofOrigin}
and is_use = #{isUse}
and series_name like '%${seriesName}%'
and series_ename like '%${seriesEname}%'
order by create_date desc
